#include <iostream>

int main() {
    for (int i = 0; i < 5; i++) {
        std::cout << "Iteration: " << i << std::endl;
    }
    return 0;
}

#include <iostream>

// Function declaration
int add(int a, int b) {
    return a + b;
}

int main() {
    int sum = add(5, 3);
    std::cout << "The sum is: " << sum << std::endl; // Outputs 8
    return 0;
}

#include <iostream>

int main() {
    int var = 20;
    int* ptr = &var; // ptr stores the address of var

    std::cout << "Value of var: " << var << std::endl;
    std::cout << "Address of var: " << &var << std::endl;
    std::cout << "Value of ptr: " << ptr << std::endl;
    std::cout << "Value at ptr address: " << *ptr << std::endl;
    return 0;
}

#include <iostream>
#include <string>

class Dog {
public:
    std::string name;
    void bark() {
        std::cout << name << " says Woof!" << std::endl;
    }
};

int main() {
    Dog myDog;
    myDog.name = "Buddy";
    myDog.bark(); // Outputs: Buddy says Woof!
    return 0;
}

#include <iostream>
#include <vector>

int main() {
    std::vector<int> numbers;
    numbers.push_back(10);
    numbers.push_back(20);
    numbers.push_back(30);

    for (int num : numbers) {
        std::cout << num << " "; // Outputs: 10 20 30 
    }
    std::cout << std::endl;
    return 0;
}
