/* This is a CSS comment */
body {
  background-color: #f0f0f0;
  font-family: Arial, sans-serif;
}

/* Element Selector */
p {
  color: blue;
}

/* Class Selector */
.highlight {
  background-color: yellow;
}

/* ID Selector */
#header {
  border-bottom: 1px solid black;
}

.box {
  width: 300px;
  padding: 20px; /* Space inside the border */
  border: 5px solid navy;
  margin: 25px; /* Space outside the border */
}

.element {
  padding: 15px; /* Creates space inside the element */
  margin: 10px; /* Creates space around the element */
}

body {
  background-color: #333;
  color: #FFF; /* White text on a dark background */
}

.container {
  display: flex;
  justify-content: space-around; /* Distributes items along the main axis */
  align-items: center; /* Aligns items along the cross axis */
}

.wrapper {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr; /* Creates three equal-width columns */
  gap: 10px; /* Creates a 10px gap between grid items */
}

/* On screens that are 600px wide or less, the background color is blue */
@media screen and (max-width: 600px) {
  body {
    background-color: blue;
  }
}

button {
  background-color: #7B61FF;
  transition: background-color 0.3s ease-in-out;
}

button:hover {
  background-color: #00E5FF;
}

:root {
  --primary-color: #7B61FF;
  --text-color: #E8EAF6;
}

p {
  color: var(--text-color);
}

a {
  color: var(--primary-color);
}
